USE acore_world;

CREATE TABLE IF NOT EXISTS `classless_achievements_which_yield_talents`
(
    `achievement_id`          INT UNSIGNED NOT NULL,
    `title_lang_en_gb`        VARCHAR(255) NOT NULL,
    `description_lang_en_gb`  VARCHAR(255) NOT NULL,
    `points`                  INT UNSIGNED NOT NULL,
    `name_lang_en_gb`         VARCHAR(255) NOT NULL,
    `achievement_category_id` INT          NOT NULL,
    `parent_category_id`      INT          NOT NULL,
    PRIMARY KEY (`achievement_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;





